mapper.select:
select `menu_id`,`parent_id`,`name`,`url`,`perms`,`component`,`type`,`icon`,`order_num`,`create_time`,`update_time`,`del_flag`
from sys_menu a

	a.del_flag = 1
	 and menu_id = #{menuId} 
	 and parent_id = #{parentId} 

		AND a.name LIKE concat('%',#{name},'%')

	 and type = #{type} 
	 and order_num = #{orderNum} 




		order by ${sort} ${order}


		order by menu_id desc



	limit #{offset}, #{limit}

select `menu_id`,`parent_id`,`name`,`url`,`perms`,`component`,`type`,`icon`,`order_num`,`create_time`,`update_time`,`del_flag`
from sys_menu a

	a.del_flag = 1
	AND a.menu_type = 0
	 and menu_id = #{menuId} 
	 and parent_id = #{parentId} 

		AND a.name LIKE concat('%',#{name},'%')

	 and type = #{type} 
	 and order_num = #{orderNum} 




		order by ${sort} ${order}


		order by menu_id desc



	limit #{offset}, #{limit}

select `menu_id`,`parent_id`,`name`,`url`,`perms`,`component`,`type`,`icon`,`order_num`,`create_time`,`update_time`,`del_flag`
from sys_menu a

	a.del_flag = 1
	AND a.menu_type = 1
	 and menu_id = #{menuId} 
	 and parent_id = #{parentId} 

		AND a.name LIKE concat('%',#{name},'%')

	 and type = #{type} 
	 and order_num = #{orderNum} 




		order by ${sort} ${order}


		order by menu_id desc



	limit #{offset}, #{limit}

select count(*) from sys_menu a

	a.del_flag = 1
	 and menu_id = #{menuId} 
	 and parent_id = #{parentId} 

		AND a.name LIKE concat('%',#{name},'%')

	 and type = #{type} 
	 and order_num = #{orderNum}

mapper.delete:
update sys_menu set del_flag = 0
where menu_id in

	#{menuId}
