mapper.sql:
where del_flag = 1

  and plan_title like  concat('%', #{planTitle}, '%')


  and builder_id = #{userId}

mapper.update:
update assist_plan
set del_flag = 0
where id = #{id,jdbcType=BIGINT}

mapper.insert:
insert into assist_plan (plan_title, plan_content,
  plan_destination, plan_score, builder_id,del_flag
  )
values (#{planTitle,jdbcType=VARCHAR}, #{planContent,jdbcType=VARCHAR},
  #{planDestination,jdbcType=VARCHAR}, #{planScore,jdbcType=REAL}, #{userId,jdbcType=BIGINT},1
  )
